import React from 'react';
import MetricCard from './MetricCard';
import ComparisonBar from './ComparisonBar';

const MetricsDisplay = ({ metrics, history, language }) => {
  if (!metrics) {
    return (
      <div className="text-center py-8">
        <p className="text-lg text-gray-600 hindi-font">
          {language === 'en' ? 'No data available' : '‡§ï‡•ã‡§à ‡§°‡•á‡§ü‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§π‡•Ä‡§Ç'}
        </p>
      </div>
    );
  }

  const metricConfig = [
    {
      key: 'householdsRegistered',
      title: language === 'en' ? 'Households Registered' : '‡§™‡§Ç‡§ú‡•Ä‡§ï‡•É‡§§ ‡§™‡§∞‡§ø‡§µ‡§æ‡§∞',
      dataKey: 'householdsRegistered',
      tooltip: language === 'en' 
        ? 'Number of households registered under MGNREGA in this district'
        : '‡§á‡§∏ ‡§ú‡§ø‡§≤‡•á ‡§Æ‡•á‡§Ç ‡§Æ‡§®‡§∞‡•á‡§ó‡§æ ‡§ï‡•á ‡§§‡§π‡§§ ‡§™‡§Ç‡§ú‡•Ä‡§ï‡•É‡§§ ‡§™‡§∞‡§ø‡§µ‡§æ‡§∞‡•ã‡§Ç ‡§ï‡•Ä ‡§∏‡§Ç‡§ñ‡•ç‡§Ø‡§æ',
      icon: 'üè†'
    },
    {
      key: 'personDaysGenerated',
      title: language === 'en' ? 'Person-Days Generated' : '‡§µ‡•ç‡§Ø‡§ï‡•ç‡§§‡§ø-‡§¶‡§ø‡§µ‡§∏ ‡§â‡§§‡•ç‡§™‡§®‡•ç‡§®',
      dataKey: 'personDaysGenerated',
      tooltip: language === 'en'
        ? 'Total employment days generated for workers'
        : '‡§∂‡•ç‡§∞‡§Æ‡§ø‡§ï‡•ã‡§Ç ‡§ï‡•á ‡§≤‡§ø‡§è ‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§ï‡•Å‡§≤ ‡§∞‡•ã‡§ú‡§ó‡§æ‡§∞ ‡§¶‡§ø‡§µ‡§∏',
      icon: 'üë•'
    },
    {
      key: 'totalWagesPaid',
      title: language === 'en' ? 'Total Wages Paid' : '‡§ï‡•Å‡§≤ ‡§Æ‡§ú‡§¶‡•Ç‡§∞‡•Ä ‡§≠‡•Å‡§ó‡§§‡§æ‡§®',
      dataKey: 'totalWagesPaid',
      tooltip: language === 'en'
        ? 'Total wages paid to workers in Indian Rupees'
        : '‡§∂‡•ç‡§∞‡§Æ‡§ø‡§ï‡•ã‡§Ç ‡§ï‡•ã ‡§≠‡•Å‡§ó‡§§‡§æ‡§® ‡§ï‡•Ä ‡§ó‡§à ‡§ï‡•Å‡§≤ ‡§Æ‡§ú‡§¶‡•Ç‡§∞‡•Ä (‡§∞‡•Å‡§™‡§Ø‡•á ‡§Æ‡•á‡§Ç)',
      icon: 'üí∞',
      isCurrency: true
    },
    {
      key: 'worksCompleted',
      title: language === 'en' ? 'Works Completed' : '‡§ï‡§æ‡§∞‡•ç‡§Ø ‡§™‡•Ç‡§∞‡•ç‡§£',
      dataKey: 'worksCompleted',
      tooltip: language === 'en'
        ? 'Number of development works completed'
        : '‡§™‡•Ç‡§∞‡•ç‡§£ ‡§ï‡§ø‡§è ‡§ó‡§è ‡§µ‡§ø‡§ï‡§æ‡§∏ ‡§ï‡§æ‡§∞‡•ç‡§Ø‡•ã‡§Ç ‡§ï‡•Ä ‡§∏‡§Ç‡§ñ‡•ç‡§Ø‡§æ',
      icon: '‚úÖ'
    },
    {
      key: 'averageWagePerPersonDay',
      title: language === 'en' ? 'Average Wage per Day' : '‡§™‡•ç‡§∞‡§§‡§ø‡§¶‡§ø‡§® ‡§î‡§∏‡§§ ‡§Æ‡§ú‡§¶‡•Ç‡§∞‡•Ä',
      dataKey: 'averageWagePerPersonDay',
      tooltip: language === 'en'
        ? 'Average wage paid per person per day in Indian Rupees'
        : '‡§™‡•ç‡§∞‡§§‡§ø ‡§µ‡•ç‡§Ø‡§ï‡•ç‡§§‡§ø ‡§™‡•ç‡§∞‡§§‡§ø‡§¶‡§ø‡§® ‡§≠‡•Å‡§ó‡§§‡§æ‡§® ‡§ï‡•Ä ‡§ó‡§à ‡§î‡§∏‡§§ ‡§Æ‡§ú‡§¶‡•Ç‡§∞‡•Ä (‡§∞‡•Å‡§™‡§Ø‡•á ‡§Æ‡•á‡§Ç)',
      icon: 'üìä',
      isCurrency: true
    }
  ];

  return (
    <div>
      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
        {metricConfig.map((config) => (
          <MetricCard
            key={config.key}
            title={config.title}
            value={metrics[config.key]}
            history={history}
            dataKey={config.dataKey}
            language={language}
            tooltip={config.tooltip}
            icon={config.icon}
            isCurrency={config.isCurrency}
          />
        ))}
      </div>

      {/* Comparison Section */}
      <div className="bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-xl font-semibold mb-6 text-center hindi-font">
          {language === 'en' ? 'District vs State Average' : '‡§ú‡§ø‡§≤‡§æ ‡§¨‡§®‡§æ‡§Æ ‡§∞‡§æ‡§ú‡•ç‡§Ø ‡§î‡§∏‡§§'}
        </h3>
        <div className="space-y-6">
          {metricConfig.map((config) => (
            <ComparisonBar
              key={config.key}
              title={config.title}
              districtValue={metrics[config.key]}
              stateValue={metrics.stateAverage ? metrics.stateAverage[config.key] : 0}
              language={language}
              isCurrency={config.isCurrency}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MetricsDisplay;
